r.connect({host:'localhost', port:28015}, function(conn) {
 ...
}, function() {
 throw 'Connect failed';
})

conn.run(r.table('marvel'), function(hero) {
  ...
  return true; // callback gets called again; return false to stop being invoked
})

r.table('marvel').runp();
// Is equivalent to:
var cur = r.table('marvel').run();
cur.collect(function(res) {
 console.log(res);
});

var cur = r.table('marvel').run();
cur.next(function(row) {
 return processRow(row);
});

var cur = r.table('bigTable').run();
cur.next(function(row) {
 return processRow(row);
});

var cur = r.table('smallTable').run();
cur.collect(function(results) {
 for(var i in results) {
  processRow(results[i]);
 }
});

r.table('marvel').map(function(hero) {
 return hero('monstersKilled')).add(hero('superVillainsVanquished'))
}).reduce(0, function(acc, val) {
 return acc.add(val)
}).run()

r.table('marvel').map {|hero|
  hero[:monsters_killed] + hero[:super_villains_vanquished]
}.reduce(0) {|acc, val|
  acc + val
}.run

r.table('marvel').groupedMapReduce(function(hero) {
 return hero('weightClass')
}, function(hero) {
 return hero.pick('name', 'strength')
}, {name:'none', strength:0}, function(acc, hero) {
 return r.branch(acc('strength').lt(hero('strength')), hero, acc)
}).run()

r.table('marvel').grouped_map_reduce(lambda {|hero|
 hero[:weight_class]
}, lambda {|hero|
 hero.pick(:name, :strength)
}, {:name => 'none', :strength => 0}, lambda {|acc, hero|
 r.branch(acc[:strength] < hero[:strength], hero, acc)
}).run

r.let(:ironman => r.table('marvel').get('IronMan'), :thor => r.table('marvel').get('Thor')) {
  r.branch(r.letvar(:ironman)[:manliness] > r.letvar(:thor)[:manliness],
    r.letvar(:ironman),
    r.letvar(:thor))
}.run

r.let(:ironman => r.table('marvel').get('IronMan')) {
  r.branch(r.letvar(:ironman)[:victories] < r.letvar(:ironman)[:battles],
    r.error('impossible code path'),
    r.letvar('ironman'))
}.run
