      T e = relative_error(val, T(small_values[k]));
      unsigned err = e.template convert_to<unsigned>();
      if(err > max_err)
      {
         max_err = err;
      }
      val = boost::multiprecision::sinh(-v);
      e = relative_error(val, T(-T(small_values[k])));
