section.programlisting:
struct Hello 
{
  void operator()() const
  {
    std::cout << "Hello";
  }
};

struct World
{
  void operator()() const
  {
    std::cout << ", World!" << std::endl;
  }
};

entry.programlisting:
boost::signal<void ()> sig;

sig.connect(Hello());
sig.connect(World());

sig();

boost::signal0<void> sig;

sig.connect(Hello());
sig.connect(World());

sig();

boost::signal<void ()> sig;
sig.connect(1, World());
sig.connect(0, Hello());
sig();

boost::signal0<void> sig;
sig.connect(1, World());
sig.connect(0, Hello());
sig();

